      } else {
        req.session.cookie.maxAge = 24 * 60 * 60 * 1000; // 1 dia
      }

      // Salvar a sess√£o explicitamente antes de responder
      req.session.save((err) => {
        if (err) {
          console.error("‚ùå Session save error:", err);
        }

        console.log("üîÑ Login successful, session saved:", { 
          userId: user.id, 
