import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Download, X, Smartphone } from "lucide-react";

interface BeforeInstallPromptEvent extends Event {
  readonly platforms: string[];
  readonly userChoice: Promise<{
    outcome: 'accepted' | 'dismissed';
    platform: string;
  }>;
  prompt(): Promise<void>;
}

export function InstallPWA() {
  const [deferredPrompt, setDeferredPrompt] = useState<BeforeInstallPromptEvent | null>(null);
  const [showInstallCard, setShowInstallCard] = useState(false);
  const [isIOS, setIsIOS] = useState(false);
  const [isStandalone, setIsStandalone] = useState(false);

  useEffect(() => {
    const isIOSDevice = /iPad|iPhone|iPod/.test(navigator.userAgent);
    const isInStandaloneMode = window.matchMedia('(display-mode: standalone)').matches;

    setIsIOS(isIOSDevice);
    setIsStandalone(isInStandaloneMode);

    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(e as BeforeInstallPromptEvent);

      // Mostrar o card de instala√ß√£o ap√≥s alguns segundos
      setTimeout(() => {
        if (!isInStandaloneMode) {
          setShowInstallCard(true);
        }
      }, 3000);
    };

    const handleAppInstalled = () => {
      console.log('PWA foi instalado');
      setShowInstallCard(false);
      setDeferredPrompt(null);
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    window.addEventListener('appinstalled', handleAppInstalled);

    // Para iOS - mostrar instru√ß√µes depois de um tempo
    if (isIOSDevice && !isInStandaloneMode) {
      setTimeout(() => {
        setShowInstallCard(true);
      }, 5000);
    }

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      window.removeEventListener('appinstalled', handleAppInstalled);
    };
  }, []);

  const handleInstallClick = async () => {
    if (deferredPrompt) {
      deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;

      if (outcome === 'accepted') {
        console.log('Usuario aceitou instalar a PWA');
      } else {
        console.log('Usuario rejeitou instalar a PWA');
      }

      setDeferredPrompt(null);
      setShowInstallCard(false);
    }
  };

  const handleDismiss = () => {
    setShowInstallCard(false);
  };

  // N√£o mostrar se j√° est√° instalado
  if (isStandalone) {
    return null;
  }

  // N√£o mostrar se n√£o h√° prompt dispon√≠vel e n√£o √© iOS
  if (!deferredPrompt && !isIOS) {
    return null;
  }

  // N√£o mostrar o card se foi dismissado
  if (!showInstallCard) {
    return null;
  }

  return (
    <div className="fixed bottom-4 left-4 right-4 z-50 md:left-auto md:right-4 md:w-80">
      <Card className="bg-gradient-to-r from-baby-pink to-baby-blue border-0 shadow-lg">
        <CardContent className="p-4">
          <div className="flex items-start justify-between">
            <div className="flex items-center space-x-3">
              <div className="bg-white/20 p-2 rounded-full">
                <Smartphone className="h-5 w-5 text-white" />
              </div>
              <div className="flex-1">
                <h3 className="text-white font-medium text-sm">
                  Instalar App
                </h3>
                <p className="text-white/90 text-xs">
                  {isIOS 
                    ? 'Toque no √≠cone de compartilhar e "Adicionar √† Tela Inicial"'
                    : 'Acesse offline e tenha uma experi√™ncia completa'
                  }
                </p>
              </div>
            </div>
            <button
              onClick={handleDismiss}
              className="text-white/80 hover:text-white"
              data-testid="button-dismiss-install"
            >
              <X size={18} />
            </button>
          </div>

          {!isIOS && (
            <div className="mt-3 flex space-x-2">
              <Button
                onClick={handleInstallClick}
                size="sm"
                className="bg-white/20 hover:bg-white/30 text-white border-0 flex-1"
                data-testid="button-install-pwa"
              >
                <Download size={16} className="mr-1" />
                Instalar
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
        allowedFileTypes: ['image/*'],
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
        setShowModal(false);
      })
  );

  return (
    <div>
      <Button 
        type="button"
        onClick={() => setShowModal(true)} 
        className={buttonClassName}
        variant="outline"
      >
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
        showProgressDetails={true}
        locale={{
          strings: {
            closeModal: 'Fechar',
            addMore: 'Adicionar mais',
            importFrom: 'Importar de',
            dashboardWindowTitle: 'Upload de Imagem',
            dashboardTitle: 'Selecione sua foto de perfil',
            browseFiles: 'procure no dispositivo',
          }
        }}
      />
    </div>
  );
}

client/src/components/progress-circle.ts.content:
interface ProgressCircleProps {
  percentage: number;
  size?: number;
  strokeWidth?: number;
  className?: string;
}

export default function ProgressCircle({ 
  percentage, 
  size = 128, 
  strokeWidth = 8,
  className 
}: ProgressCircleProps) {
  const radius = (size - strokeWidth) / 2;
  const circumference = 2 * Math.PI * radius;
  const strokeDasharray = circumference;
  const strokeDashoffset = circumference - (percentage / 100) * circumference;

  return (
    <div className={`relative ${className}`} style={{ width: size, height: size }}>
      <svg 
        className="transform -rotate-90" 
        width={size} 
        height={size}
        viewBox={`0 0 ${size} ${size}`}
      >
        <defs>
          <linearGradient id="progressGradient" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="var(--baby-pink-dark)" />
            <stop offset="100%" stopColor="var(--baby-blue-dark)" />
          </linearGradient>
        </defs>

        {/* Background circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          fill="none"
          stroke="#E5E7EB"
          strokeWidth={strokeWidth}
        />

        {/* Progress circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          fill="none"
          stroke="url(#progressGradient)"
          strokeWidth={strokeWidth}
          strokeDasharray={strokeDasharray}
          strokeDashoffset={strokeDashoffset}
          strokeLinecap="round"
          className="progress-ring transition-all duration-500 ease-in-out"
        />
      </svg>

      {/* Percentage text */}
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="text-center">
          <span className="text-2xl font-bold text-charcoal" data-testid="text-progress-percentage">
            {percentage}%
          </span>
          <div className="text-xs text-gray-600">completo</div>
        </div>
      </div>
    </div>
  );
}

client/src/components/three-d-baby.tsx.content:
import { useEffect, useRef, useState } from 'react';
import { Baby } from 'lucide-react';

interface ThreeDBabyProps {
  week: number;
  size?: number;
}

export default function ThreeDBaby({ week, size = 80 }: ThreeDBabyProps) {
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsAnimating(prev => !prev);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  // Calculate baby development stage based on week
  const getStageColor = (week: number) => {
    if (week <= 12) return "text-baby-pink-dark";
    if (week <= 24) return "text-baby-blue-dark";
    return "text-coral";
  };

  const getBabyIcon = (week: number) => {
    return <Baby className={`${getStageColor(week)} transition-colors duration-500`} />;
  };

  return (
    <div 
      className={`
        w-20 h-20 bg-white bg-opacity-20 rounded-full 
        flex items-center justify-center 
        ${isAnimating ? 'animate-bounce-slow' : 'animate-pulse-slow'}
        transition-all duration-1000
      `}
      style={{ width: size, height: size }}
      data-testid="three-d-baby"
    >
      <div className="text-2xl">
        {getBabyIcon(week)}
      </div>
    </div>
  );
}

