                </CardContent>
              </Card>

              <Card className="text-center">
                <CardContent className="p-4">
                  <div className="text-2xl font-bold text-orange-600">
                    {Math.floor(todayCount / 24 * 10) / 10}
                  </div>
                  <div className="text-xs text-gray-600">M√©dia/Hora</div>
                </CardContent>
              </Card>
            </div>

            {/* Weekly Chart */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <TrendingUp className="w-4 h-4 mr-2" />
                  Atividade Semanal
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <AreaChart data={weeklyData}>
                      <defs>
                        <linearGradient id="kickGradient" x1="0" y1="0" x2="0" y2="1">
                          <stop offset="5%" stopColor="#EC4899" stopOpacity={0.8}/>
                          <stop offset="95%" stopColor="#EC4899" stopOpacity={0.1}/>
                        </linearGradient>
                      </defs>
                      <XAxis dataKey="day" />
                      <YAxis />
                      <CartesianGrid strokeDasharray="3 3" />
                      <Tooltip 
                        formatter={(value: number) => [`${value} movimentos`, 'Total']}
                        labelFormatter={(day) => `${day}`}
                      />
                      <Area 
                        type="monotone" 
                        dataKey="kicks" 
                        stroke="#EC4899" 
                        fillOpacity={1} 
                        fill="url(#kickGradient)" 
                      />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* History Tab */}
          <TabsContent value="history">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Calendar className="w-4 h-4 mr-2" />
                  Hist√≥rico Completo
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {kicks.concat(kickData?.records || []).slice(0, 20).map((kick, index) => (
                    <div key={`${kick.id}-${index}`} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center space-x-4">
                        <span className="text-2xl">
                          {movementTypes.find(m => m.type === kick.type)?.icon}
                        </span>
                        <div>
                          <div className="font-medium">
                            {movementTypes.find(m => m.type === kick.type)?.label}
                          </div>
                          <div className="text-sm text-gray-600">
                            {new Date(kick.time).toLocaleString('pt-BR')} ‚Ä¢ {
                              intensityOptions.find(i => i.level === kick.intensity)?.label
                            }
                          </div>
                        </div>
                      </div>
                      <Badge variant="outline">#{index + 1}</Badge>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

