                      <div className="flex-1">
                        <h4 className="font-semibold text-gray-600 mb-1">{medication.name}</h4>
                        <div className="space-y-1 text-sm text-gray-500">
                          {medication.dosage && (
                            <div className="flex items-center">
                              <Pill className="h-3 w-3 mr-1" />
