import { useState } from "react";
import { useLocation } from "wouter";
import { useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ArrowLeft, Calendar, Info, Baby, Heart, User, Upload } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";

export default function PregnancySetup() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    lastMenstrualPeriod: "",
    dueDate: "",
  });
  const [calculationMethod, setCalculationMethod] = useState<"lmp" | "due">("lmp");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [profilePhoto, setProfilePhoto] = useState<string>("");

  const calculateDueDate = (lmp: string) => {
    const lmpDate = new Date(lmp);
    const dueDate = new Date(lmpDate);
    dueDate.setDate(dueDate.getDate() + 280); // 40 semanas
    return dueDate.toISOString().split('T')[0];
  };

  const calculateLMP = (due: string) => {
    const dueDate = new Date(due);
    const lmpDate = new Date(dueDate);
    lmpDate.setDate(lmpDate.getDate() - 280); // 40 semanas atr√°s
    return lmpDate.toISOString().split('T')[0];
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      let finalDueDate = formData.dueDate;
      let finalLMP = formData.lastMenstrualPeriod;

      if (calculationMethod === "lmp" && formData.lastMenstrualPeriod) {
        finalDueDate = calculateDueDate(formData.lastMenstrualPeriod);
      } else if (calculationMethod === "due" && formData.dueDate) {
        finalLMP = calculateLMP(formData.dueDate);
      }

      await apiRequest("POST", "/api/pregnancies", {
        dueDate: finalDueDate,
        lastMenstrualPeriod: finalLMP,
        isActive: true,
      });

      // Invalidar cache da query de gravidez ativa
      queryClient.invalidateQueries({ queryKey: ["/api/pregnancies/active"] });

      // Redirecionar para o dashboard ap√≥s salvar
      setLocation("/");
    } catch (error: any) {
      setError(error.message || "Erro ao salvar dados da gravidez");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 gradient-bg">
      <Card className="w-full max-w-md glass-effect shadow-2xl">
        <CardHeader className="text-center">
          <div className="flex items-center justify-between">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setLocation("/login")}
              className="text-gray-600 hover:text-gray-800"
              data-testid="button-back"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <div className="flex-1 flex flex-col items-center">
              <div className="mb-3">
                <div className="w-20 h-20 border-4 border-white shadow-lg rounded-full overflow-hidden bg-gradient-to-r from-baby-pink to-baby-blue flex items-center justify-center relative">
                  {profilePhoto || user?.profilePhotoUrl ? (
                    <img 
                      src={profilePhoto || user.profilePhotoUrl} 
                      alt={user.name} 
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <User className="w-10 h-10 text-white" />
                  )}
                  <div className="absolute -bottom-1 -right-1">
                    <ObjectUploader
                      maxNumberOfFiles={1}
                      maxFileSize={5242880}
                      onGetUploadParameters={async () => {
                        const response = await apiRequest("POST", "/api/uploads/profile-photo");
                        return {
                          method: "PUT" as const,
                          url: response.uploadURL,
                        };
                      }}
                      onComplete={async (result) => {
                        if (result.successful?.[0]) {
                          const uploadURL = result.successful[0].uploadURL;
                          console.log("Upload URL:", uploadURL);
                          setProfilePhoto(uploadURL);

                          try {
                            const response = await apiRequest("PATCH", "/api/auth/profile", {
                              profilePhotoUrl: uploadURL,
                            });
                            console.log("Profile updated:", response);
                          } catch (error) {
                            console.error("Erro ao salvar foto:", error);
                            setError("Erro ao salvar foto de perfil");
                          }
                        }
                      }}
                      buttonClassName="w-8 h-8 rounded-full bg-white shadow-md flex items-center justify-center p-0 min-w-0 h-8"
                    >
                      <Upload className="w-4 h-4 text-baby-pink-dark" />
                    </ObjectUploader>
                  </div>
                </div>
              </div>
              <CardTitle className="text-2xl font-bold text-charcoal">
                Dados da Gravidez
              </CardTitle>
              <p className="text-gray-600 mt-2">
                Vamos calcular em que semana voc√™ est√°
              </p>
            </div>
            <div className="w-10" /> {/* Spacer para centralizar t√≠tulo */}
          </div>
        </CardHeader>

        <CardContent className="p-6">
          {error && (
            <Alert className="mb-4 border-red-200 bg-red-50">
              <Info className="h-4 w-4 text-red-600" />
              <AlertDescription className="text-red-700">
                {error}
              </AlertDescription>
            </Alert>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* M√©todo de c√°lculo */}
            <div className="space-y-3">
              <Label className="text-charcoal font-medium">Como voc√™ prefere calcular?</Label>
              <div className="flex gap-3">
                <Button
                  type="button"
                  variant={calculationMethod === "lmp" ? "default" : "outline"}
                  onClick={() => setCalculationMethod("lmp")}
                  className={`flex-1 ${
                    calculationMethod === "lmp" 
                      ? "bg-gradient-to-r from-baby-pink-dark to-baby-blue-dark text-white" 
                      : "border-gray-300"
                  }`}
                  data-testid="button-lmp"
                >
                  <Calendar className="h-4 w-4 mr-2" />
                  √öltima Menstrua√ß√£o
                </Button>
                <Button
                  type="button"
                  variant={calculationMethod === "due" ? "default" : "outline"}
                  onClick={() => setCalculationMethod("due")}
                  className={`flex-1 ${
                    calculationMethod === "due" 
                      ? "bg-gradient-to-r from-baby-pink-dark to-baby-blue-dark text-white" 
                      : "border-gray-300"
                  }`}
                  data-testid="button-due"
                >
                  <Calendar className="h-4 w-4 mr-2" />
                  Data Prevista
                </Button>
              </div>
            </div>

            {/* Data da √∫ltima menstrua√ß√£o */}
            {calculationMethod === "lmp" && (
              <div>
                <Label className="text-charcoal font-medium">
                  Data da √∫ltima menstrua√ß√£o *
                </Label>
                <Input
                  type="date"
                  value={formData.lastMenstrualPeriod}
                  onChange={(e) => setFormData(prev => ({ ...prev, lastMenstrualPeriod: e.target.value }))}
                  className="mt-1 focus:ring-2 focus:ring-baby-pink focus:border-baby-pink-dark"
                  data-testid="input-lmp"
                  required
                />
                <p className="text-sm text-gray-500 mt-1">
                  Calculamos automaticamente a data prevista do parto
                </p>
              </div>
            )}

            {/* Data prevista do parto */}
            {calculationMethod === "due" && (
              <div>
                <Label className="text-charcoal font-medium">
                  Data prevista do parto *
                </Label>
                <Input
                  type="date"
                  value={formData.dueDate}
                  onChange={(e) => setFormData(prev => ({ ...prev, dueDate: e.target.value }))}
                  className="mt-1 focus:ring-2 focus:ring-baby-pink focus:border-baby-pink-dark"
                  data-testid="input-due"
                  required
                />
                <p className="text-sm text-gray-500 mt-1">
                  Calculamos automaticamente a data da √∫ltima menstrua√ß√£o
                </p>
              </div>
            )}

            <Button
              type="submit"
              className="w-full bg-gradient-to-r from-baby-pink-dark to-baby-blue-dark hover:opacity-90 text-white font-medium py-3"
              disabled={loading || (!formData.lastMenstrualPeriod && !formData.dueDate)}
              data-testid="button-continue"
            >
              {loading ? "Salvando..." : "Continuar"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState } from "react";
import * as React from "react";
import { useAuth } from "@/hooks/useAuth";
import { usePregnancy } from "@/hooks/use-pregnancy";
import { useLocation } from "wouter";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { LoadingSpinner } from "@/components/ui/loading-spinner";
import { useToast } from "@/hooks/use-toast";
import BottomNavigation from "@/components/layout/bottom-navigation";
import { 
  ArrowLeft, 
  Camera, 
  User, 
  Save, 
  Calendar,
  Mail,
  Edit2
} from "lucide-react";

export default function Profile() {
  const { user, isLoading: authLoading } = useAuth();
  const { pregnancy, isLoading: pregnancyLoading } = usePregnancy();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || "",
    email: user?.email || "",
    birthDate: user?.birthDate ? new Date(user.birthDate).toISOString().split('T')[0] : "",
    lastMenstrualPeriod: pregnancy?.lastMenstrualPeriod ? 
      new Date(pregnancy.lastMenstrualPeriod).toISOString().split('T')[0] : ""
  });

  // Atualizar formData quando user ou pregnancy mudarem
  React.useEffect(() => {
    if (user) {
      setFormData(prev => ({
        ...prev,
        name: user.name || "",
        email: user.email || "",
        birthDate: user.birthDate ? new Date(user.birthDate).toISOString().split('T')[0] : ""
      }));
    }
  }, [user]);

  React.useEffect(() => {
    if (pregnancy?.lastMenstrualPeriod) {
      setFormData(prev => ({
        ...prev,
        lastMenstrualPeriod: new Date(pregnancy.lastMenstrualPeriod!).toISOString().split('T')[0]
      }));
    }
  }, [pregnancy]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      // Atualizar dados do usu√°rio
      const userResponse = await apiRequest("PUT", "/api/auth/profile", {
        name: data.name,
        birthDate: data.birthDate ? new Date(data.birthDate) : null
      });

      // Se tem dados de gravidez e DUM foi alterada, atualizar a gravidez
      if (pregnancy && data.lastMenstrualPeriod) {
        await apiRequest("PUT", `/api/pregnancies/${pregnancy.id}`, {
          lastMenstrualPeriod: new Date(data.lastMenstrualPeriod)
        });
      }

      return userResponse.json();
    },
    onSuccess: () => {
      toast({
        title: "Perfil atualizado!",
        description: "Suas informa√ß√µes foram salvas com sucesso.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      queryClient.invalidateQueries({ queryKey: ["/api/pregnancies/active"] });
      setIsEditing(false);
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel salvar as altera√ß√µes.",
        variant: "destructive"
      });
    }
  });

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map(word => word.charAt(0).toUpperCase())
      .slice(0, 2)
      .join("");
  };

  if (authLoading || pregnancyLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-pink-50 via-purple-50 to-blue-50">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!user) {
    setLocation("/login");
    return null;
  }

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateProfileMutation.mutate(formData);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData({
      name: user?.name || "",
      email: user?.email || "",
      birthDate: user?.birthDate ? new Date(user.birthDate).toISOString().split('T')[0] : "",
      lastMenstrualPeriod: pregnancy?.lastMenstrualPeriod ? 
        new Date(pregnancy.lastMenstrualPeriod).toISOString().split('T')[0] : ""
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-50 via-purple-50 to-blue-50 pb-20">
      <div className="p-4 pt-12">
        {/* Header */}
        <div className="flex items-center mb-8">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setLocation("/")}
            className="mr-4 hover:bg-white/10"
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4" />
          </Button>
          <h1 className="text-2xl font-bold text-gray-800">Meu Perfil</h1>
        </div>

        {/* Profile Header */}
        <Card className="glass-effect shadow-xl mb-6">
          <CardContent className="p-6">
            <div className="flex flex-col items-center space-y-4">
              <div className="relative">
                <Avatar className="w-24 h-24 bg-pink-300">
                  <AvatarFallback className="text-white text-2xl font-bold">
                    {getInitials(user.name)}
                  </AvatarFallback>
                </Avatar>
                <Button
                  type="button"
                  size="sm"
                  className="absolute -bottom-2 -right-2 w-10 h-10 rounded-full bg-purple-500 hover:bg-purple-600 p-0"
                  data-testid="button-change-photo"
                >
                  <Camera className="h-4 w-4" />
                </Button>
              </div>
              <div className="text-center">
                <h2 className="text-xl font-bold text-gray-800" data-testid="text-user-name">
                  {user.name}
                </h2>
                <p className="text-gray-600" data-testid="text-user-email">
                  {user.email}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {isEditing ? (
          /* Edit Mode */
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Personal Information */}
            <Card className="glass-effect shadow-xl">
              <CardHeader>
                <CardTitle className="text-lg text-gray-800 flex items-center">
                  <User className="h-5 w-5 mr-2 text-pink-500" />
                  Informa√ß√µes Pessoais
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="name" className="text-gray-700">Nome Completo</Label>
                  <Input
