import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useToast } from '@/hooks/use-toast';
import { Link, useLocation } from 'wouter';
import { Shield, ArrowLeft, Eye, EyeOff } from 'lucide-react';

const verifySchema = z.object({
  token: z.string().min(4, 'C√≥digo deve ter 4 n√∫meros').max(4, 'C√≥digo deve ter 4 n√∫meros'),
  newPassword: z.string().min(6, 'Senha deve ter pelo menos 6 caracteres'),
  confirmPassword: z.string().min(6, 'Confirma√ß√£o √© obrigat√≥ria')
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "Senhas n√£o conferem",
  path: ["confirmPassword"],
});

type VerifyForm = z.infer<typeof verifySchema>;

export default function VerifyTokenPage() {
  const [location, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { toast } = useToast();

  const form = useForm<VerifyForm>({
    resolver: zodResolver(verifySchema),
    defaultValues: {
      token: '',
      newPassword: '',
      confirmPassword: ''
    }
  });

  const onSubmit = async (data: VerifyForm) => {
    setIsLoading(true);
    setError('');

    try {
      // Primeiro verificar o token
      const verifyResponse = await fetch('/api/auth/verify-reset-token', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token: data.token })
      });

      if (!verifyResponse.ok) {
        const verifyData = await verifyResponse.json();
        setError(verifyData.error || 'C√≥digo inv√°lido');
        return;
      }

      // Se o token √© v√°lido, redefinir a senha
      const resetResponse = await fetch('/api/auth/reset-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          token: data.token,
          newPassword: data.newPassword
        })
      });

      const resetData = await resetResponse.json();

      if (resetResponse.ok) {
        setSuccess('Senha alterada com sucesso!');
        toast({
          title: "Sucesso!",
          description: "Sua senha foi alterada. Redirecionando para o login...",
        });

        setTimeout(() => {
          setLocation('/login');
        }, 2000);
      } else {
        setError(resetData.error || 'Erro ao alterar senha');
      }
    } catch (error) {
      setError('Erro ao processar solicita√ß√£o. Tente novamente.');
    } finally {
      setIsLoading(false);
    }
  };

  if (success) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-blue-50 flex items-center justify-center p-4">
        <Card className="w-full max-w-md bg-white/80 backdrop-blur border-0 shadow-lg">
          <CardHeader className="text-center space-y-4">
            <div className="mx-auto w-16 h-16 bg-green-500 rounded-full flex items-center justify-center">
              <span className="text-white text-2xl">‚úì</span>
            </div>
            <CardTitle className="text-2xl font-bold text-green-600">
              Senha Alterada!
            </CardTitle>
            <CardDescription className="text-gray-600">
              Sua senha foi alterada com sucesso. Redirecionando...
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-blue-50 flex items-center justify-center p-4">
      <Card className="w-full max-w-md bg-white/80 backdrop-blur border-0 shadow-lg">
        <CardHeader className="text-center space-y-4">
          <div className="mx-auto w-16 h-16 bg-gradient-to-r from-pink-500 to-blue-500 rounded-full flex items-center justify-center">
            <Shield className="w-8 h-8 text-white" />
          </div>
          <CardTitle className="text-2xl font-bold text-gray-800">
            Verificar C√≥digo
          </CardTitle>
          <CardDescription className="text-gray-600">
            Digite o c√≥digo de 4 n√∫meros recebido por email e sua nova senha
          </CardDescription>
        </CardHeader>

        <CardContent className="space-y-6">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">

              {/* Campo do C√≥digo */}
              <FormField
                control={form.control}
                name="token"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-gray-700 font-medium">
                      C√≥digo de Verifica√ß√£o (4 n√∫meros)
                    </FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="number"
                        placeholder="0000"
                        maxLength={4}
                        className="text-center text-2xl h-16 font-mono tracking-widest"
                        data-testid="input-token"
                        onChange={(e) => {
                          const value = e.target.value.slice(0, 4);
                          field.onChange(value);
                        }}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Nova Senha */}
              <FormField
                control={form.control}
                name="newPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-gray-700 font-medium">
                      Nova Senha
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          {...field}
                          type={showPassword ? "text" : "password"}
                          placeholder="Digite sua nova senha"
                          className="pr-10 h-12"
                          data-testid="input-new-password"
                        />
                        <button
                          type="button"
                          onClick={() => setShowPassword(!showPassword)}
                          className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                        >
                          {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Confirmar Senha */}
              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-gray-700 font-medium">
                      Confirmar Nova Senha
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          {...field}
                          type={showConfirmPassword ? "text" : "password"}
                          placeholder="Confirme sua nova senha"
                          className="pr-10 h-12"
                          data-testid="input-confirm-password"
                        />
                        <button
                          type="button"
                          onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                          className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                        >
                          {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              <Button
                type="submit"
                className="w-full h-12 bg-gradient-to-r from-pink-500 to-blue-500 hover:from-pink-600 hover:to-blue-600 text-white font-semibold"
                disabled={isLoading}
                data-testid="button-verify-reset"
              >
                {isLoading ? 'Processando...' : 'Verificar e Alterar Senha'}
              </Button>
            </form>
          </Form>

          <div className="text-center">
            <Link href="/reset-password">
              <button className="inline-flex items-center gap-2 text-sm text-gray-600 hover:text-pink-600 transition-colors">
                <ArrowLeft className="w-4 h-4" />
                Voltar para solicitar c√≥digo
              </button>
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/weight-tracking.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { usePregnancy } from "@/hooks/use-pregnancy";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { LoadingSpinner } from "@/components/ui/loading-spinner";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import WeightChart from "@/components/weight-chart";
import BottomNavigation from "@/components/layout/bottom-navigation";
import { ArrowLeft, Scale, Plus, TrendingUp } from "lucide-react";

export default function WeightTracking() {
  const [showAddForm, setShowAddForm] = useState(false);
  const [weight, setWeight] = useState("");
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [notes, setNotes] = useState("");
  const { user } = useAuth();
  const { pregnancy } = usePregnancy();
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: weightData, isLoading } = useQuery({
    queryKey: ["/api/weight-entries"],
    enabled: !!pregnancy,
  });

  const addWeightMutation = useMutation({
    mutationFn: async (weightEntry: any) => {
      const response = await apiRequest("POST", "/api/weight-entries", weightEntry);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/weight-entries"] });
      setShowAddForm(false);
      setWeight("");
      setDate(new Date().toISOString().split('T')[0]);
      setNotes("");
      toast({
        title: "Peso registrado!",
        description: "Seu peso foi adicionado com sucesso.",
      });
    },
    onError: (error: any) => {
      console.error("Erro ao registrar peso:", error);
      toast({
        title: "Erro ao Registrar peso",
        description: `Erro: ${error?.message || "Erro desconhecido"}. Verifique os dados e tente novamente.`,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!weight || parseFloat(weight) <= 0) {
      toast({
        title: "Erro",
        description: "Por favor, insira um peso v√°lido",
        variant: "destructive",
      });
      return;
    }

    addWeightMutation.mutate({
      weight: weight,
      date: date, // Enviar como string no formato ISO
      notes: notes.trim() || undefined,
    });
  };

  if (!user || !pregnancy) {
    setLocation("/login");
    return null;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  const entries = (weightData as any)?.entries || [];
  const records = entries; // Para compatibilidade com componentes existentes
  const latestWeight = entries.length > 0 ? entries[0] : null;

  // Calculate weight gain from first record
  const firstWeight = records.length > 0 ? records[records.length - 1].weight : null;
  const weightGain = latestWeight && firstWeight ? 
    parseFloat(latestWeight.weight) - parseFloat(firstWeight.weight) : 0;

  return (
    <div className="min-h-screen bg-cream pb-20">
      <div className="p-4 pt-12">
        <div className="flex items-center justify-between mb-6">
          <Button 
            variant="ghost" 
            size="icon"
            className="rounded-full bg-white shadow-lg"
            onClick={() => setLocation("/")}
            data-testid="button-back"
          >
            <ArrowLeft className="h-5 w-5 text-charcoal" />
          </Button>
          <h2 className="text-xl font-bold text-charcoal" data-testid="text-page-title">
            Controle de Peso
          </h2>
          <Button 
            variant="ghost" 
            size="icon"
            className="rounded-full bg-baby-pink-dark shadow-lg"
            onClick={() => setShowAddForm(true)}
            data-testid="button-add-weight"
          >
            <Plus className="h-5 w-5 text-white" />
          </Button>
        </div>

        {/* Current weight display */}
        <Card className="glass-effect shadow-xl mb-6">
          <CardContent className="p-6">
            <div className="text-center">
              <h3 className="text-lg font-semibold text-charcoal mb-2">Peso Atual</h3>
              <div className="text-3xl font-bold text-baby-pink-dark" data-testid="text-current-weight">
                {latestWeight ? `${latestWeight.weight} kg` : "N√£o registrado"}
              </div>
              {weightGain !== 0 && (
                <div className="text-sm text-gray-600 mt-1" data-testid="text-weight-gain">
                  {weightGain > 0 ? '+' : ''}{weightGain.toFixed(1)} kg desde o in√≠cio
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Weight chart */}
        {records.length > 0 && (
          <Card className="shadow-lg mb-6">
            <CardHeader>
              <CardTitle className="flex items-center text-charcoal">
                <TrendingUp className="mr-2 h-5 w-5" />
                Progresso
              </CardTitle>
            </CardHeader>
            <CardContent>
              <WeightChart records={records} />
            </CardContent>
          </Card>
        )}

        {/* Weight goal */}
        <Card className="shadow-lg mb-6">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-charcoal mb-4 flex items-center">
              <Scale className="mr-2 h-5 w-5" />
              Meta de Peso
            </h3>
            <div className="flex items-center justify-between">
              <div>
                <span className="text-sm text-gray-600">Meta total: </span>
                <span className="font-semibold" data-testid="text-weight-goal">12-18 kg</span>
              </div>
              <div className="text-right">
                <div className="text-sm text-gray-600">Restante</div>
                <div className="font-semibold text-baby-blue-dark" data-testid="text-weight-remaining">
                  {weightGain < 12 ? `${(12 - weightGain).toFixed(1)}-${(18 - weightGain).toFixed(1)} kg` : 'Meta atingida'}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Recent records */}
        {records.length > 0 && (
          <Card className="shadow-lg">
            <CardHeader>
              <CardTitle className="text-charcoal">Registros Recentes</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {records.slice(0, 5).map((record: any) => (
                  <div key={record.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div>
                      <div className="font-medium" data-testid={`text-record-weight-${record.id}`}>
                        {record.weight} kg
                      </div>
                      <div className="text-sm text-gray-500" data-testid={`text-record-date-${record.id}`}>
                        üìÖ {new Date(record.date).toLocaleDateString('pt-BR')}
                      </div>
                      {record.createdAt && (
                        <div className="text-xs text-gray-400" data-testid={`text-record-created-${record.id}`}>
                          üïí Registrado: {new Date(record.createdAt).toLocaleDateString('pt-BR')} √†s {new Date(record.createdAt).toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' })}
                        </div>
                      )}
                    </div>
                    {record.notes && (
                      <div className="text-sm text-gray-600 max-w-32 truncate" data-testid={`text-record-notes-${record.id}`}>
                        {record.notes}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Add weight modal */}
      {showAddForm && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-md">
            <CardHeader>
              <CardTitle className="text-charcoal">Registrar Peso</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4" data-testid="form-add-weight">
                <div>
                  <Label htmlFor="weight" className="text-charcoal font-medium">
                    Peso (kg)
                  </Label>
                  <Input
                    id="weight"
                    type="number"
                    step="0.1"
                    placeholder="Ex: 65.5"
                    value={weight}
                    onChange={(e) => setWeight(e.target.value)}
                    className="focus:ring-2 focus:ring-baby-pink focus:border-baby-pink-dark"
                    data-testid="input-weight"
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="date" className="text-charcoal font-medium">
                    Data
                  </Label>
                  <Input
                    id="date"
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    className="focus:ring-2 focus:ring-baby-pink focus:border-baby-pink-dark"
                    data-testid="input-date"
                    required
                  />
                </div>

                <div>
                  <Label htmlFor="notes" className="text-charcoal font-medium">
                    Observa√ß√µes (opcional)
                  </Label>
                  <Textarea
                    id="notes"
                    placeholder="Ex: Ap√≥s o almo√ßo"
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    className="focus:ring-2 focus:ring-baby-pink focus:border-baby-pink-dark"
                    data-testid="input-notes"
                  />
                </div>

                <div className="flex space-x-3">
                  <Button 
                    type="button" 
                    variant="outline"
                    onClick={() => setShowAddForm(false)}
                    className="flex-1"
                    data-testid="button-cancel"
                  >
                    Cancelar
                  </Button>
                  <Button 
                    type="submit" 
                    className="flex-1 bg-gradient-to-r from-baby-pink-dark to-baby-blue-dark hover:opacity-90"
                    disabled={addWeightMutation.isPending}
                    data-testid="button-save"
                  >
                    {addWeightMutation.isPending ? (
                      <LoadingSpinner size="sm" className="mr-2" />
                    ) : (
                      <Scale className="mr-2 h-4 w-4" />
                    )}
                    Salvar
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </div>
      )}

      <BottomNavigation />
    </div>
  );
}

