client/src/components/Layout.tsx.content:
import { useLocation } from 'wouter';
import { cn } from '@/lib/utils';
import { useAuth } from '@/hooks/useAuth';
import { authManager } from '@/lib/auth';
import BottomNavigation from '@/components/layout/bottom-navigation';

interface LayoutProps {
  children: React.ReactNode;
  className?: string;
}

export function Layout({ children, className }: LayoutProps) {
  const [location] = useLocation();
  const { user, isLoading } = useAuth();

  // P√°ginas que n√£o devem ter layout (login, setup, etc.)
  const noLayoutPages = ['/login', '/reset-password', '/forgot-password', '/setup', '/pregnancy-setup'];
  const shouldHideLayout = noLayoutPages.some(page => location.startsWith(page));

  // Se est√° carregando, mostrar loading
  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-50 to-blue-50">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-pink-300 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Carregando...</p>
        </div>
      </div>
    );
  }

  // Se n√£o est√° logado e n√£o est√° em p√°gina p√∫blica, redirecionar para login
  if (!user && !shouldHideLayout) {
    console.log("üîÑ Redirecting to login - user not authenticated");
    // For√ßar redirecionamento imediato sem cache
    window.location.replace('/login');
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-50 to-blue-50">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-pink-300 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Redirecionando...</p>
        </div>
      </div>
    );
  }

  if (shouldHideLayout) {
    return <>{children}</>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50/50 via-white to-blue-50/50">
      {/* Conte√∫do principal - sem sidebar lateral */}
      <div className="min-h-screen">
        <main className={cn(
          'min-h-screen pb-20', // Adicionar padding-bottom para navega√ß√£o
          className
        )}>
          {children}
        </main>

        {/* Navega√ß√£o inferior */}
        <BottomNavigation />
      </div>
    </div>
  );
}

export default Layout;

