import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

client/src/components/PWAInstallPrompt.t.content:
import { useState, useEffect } from "react";
import { X, Download, Smartphone } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

interface BeforeInstallPromptEvent extends Event {
  prompt(): Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
}

export function PWAInstallPrompt() {
  const [deferredPrompt, setDeferredPrompt] = useState<BeforeInstallPromptEvent | null>(null);
  const [showInstallPrompt, setShowInstallPrompt] = useState(false);
  const [isStandalone, setIsStandalone] = useState(false);

  useEffect(() => {
    console.log('üîß PWA Install Prompt: Iniciando verifica√ß√µes...');

    // Para depura√ß√£o - sempre limpar o localStorage
    localStorage.removeItem('pwa-prompt-dismissed');
    console.log('üßπ localStorage limpo para depura√ß√£o');

    // Verificar se foi dispensado recentemente (TEMPORARIAMENTE DESABILITADO)
    // const dismissed = localStorage.getItem('pwa-prompt-dismissed');
    // if (dismissed) {
    //   const dismissedTime = parseInt(dismissed);
    //   const now = Date.now();
    //   const twentyFourHours = 24 * 60 * 60 * 1000;
    //   
    //   if (now - dismissedTime < twentyFourHours) {
    //     console.log('‚è∞ PWA Prompt dispensado h√° menos de 24h');
    //     return; // N√£o mostrar se foi dispensado h√° menos de 24 horas
    //   }
    // }

    // Verificar se j√° est√° instalado como PWA
    const isInStandaloneMode = window.matchMedia('(display-mode: standalone)').matches;
    const isIOSStandalone = (window.navigator as any).standalone === true;
    setIsStandalone(isInStandaloneMode || isIOSStandalone);

    console.log('üì± PWA Status:', {
      isInStandaloneMode,
      isIOSStandalone,
      userAgent: navigator.userAgent,
      isIOS: /iPad|iPhone|iPod/.test(navigator.userAgent)
    });

    // Se j√° estiver instalado, n√£o mostrar
    if (isInStandaloneMode || isIOSStandalone) {
      console.log('‚úÖ PWA j√° instalado - n√£o mostrar prompt');
      return;
    }

    // Listener para o evento de instala√ß√£o (Android/Chrome)
    const handleBeforeInstallPrompt = (e: Event) => {
      console.log('üéØ beforeinstallprompt capturado!', e);
      e.preventDefault();
      setDeferredPrompt(e as BeforeInstallPromptEvent);

      // Mostrar o prompt ap√≥s 3 segundos
      setTimeout(() => {
        console.log('üì≤ Mostrando prompt de instala√ß√£o...');
        setShowInstallPrompt(true);
      }, 3000);
    };

    // Para dispositivos que suportam PWA nativo
    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    console.log('üëÇ Listener beforeinstallprompt adicionado');

    // Para iOS e outros navegadores, mostrar ap√≥s 3 segundos
    const timer = setTimeout(() => {
      if (!isInStandaloneMode && !isIOSStandalone) {
        console.log('‚è≥ Timer ativado - mostrando prompt (sem beforeinstallprompt)');
        setShowInstallPrompt(true);
      }
    }, 3000);

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      clearTimeout(timer);
      console.log('üßπ PWA listeners removidos');
    };
  }, []);

  const handleInstallClick = async () => {
    console.log('üéØ handleInstallClick chamado, deferredPrompt:', deferredPrompt);
    if (!deferredPrompt) {
      console.log('‚ùå Nenhum deferredPrompt dispon√≠vel');
      return;
    }

    try {
      console.log('üì≤ Chamando deferredPrompt.prompt()...');
      await deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;

      console.log('üë§ Escolha do usu√°rio:', outcome);
      if (outcome === 'accepted') {
        console.log('‚úÖ PWA instalado com sucesso');
      } else {
        console.log('‚ùå Usu√°rio recusou instalar PWA');
      }

      setDeferredPrompt(null);
      setShowInstallPrompt(false);
    } catch (error) {
      console.error('üí• Erro ao instalar PWA:', error);
    }
  };

  const handleDismiss = () => {
    setShowInstallPrompt(false);
    // N√£o mostrar novamente por 24 horas
    localStorage.setItem('pwa-prompt-dismissed', Date.now().toString());
  };

  // Detectar iOS para instru√ß√µes especiais
  const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);

  console.log('üîç Render PWAInstallPrompt:', {
    isStandalone,
    showInstallPrompt,
    deferredPrompt: !!deferredPrompt,
    isIOS,
    willShow: !isStandalone && showInstallPrompt
  });

  if (isStandalone || !showInstallPrompt) {
    return null;
  }

  return (
    <Dialog open={showInstallPrompt} onOpenChange={setShowInstallPrompt}>
      <DialogContent className="sm:max-w-md mx-4">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-pink-100 dark:bg-pink-900 rounded-full flex items-center justify-center">
                <Smartphone className="w-6 h-6 text-pink-600 dark:text-pink-400" />
              </div>
              <div>
                <DialogTitle className="text-left">Instalar Mama Care</DialogTitle>
                <DialogDescription className="text-left text-sm">
                  Acesse rapidamente do seu celular
                </DialogDescription>
              </div>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleDismiss}
              className="h-8 w-8 p-0"
              data-testid="button-dismiss-install"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        <div className="space-y-4">
          <div className="text-sm text-muted-foreground">
            <p>Instale o Mama Care no seu celular para:</p>
            <ul className="mt-2 space-y-1 list-disc list-inside">
              <li>Acesso r√°pido da tela inicial</li>
              <li>Funciona sem internet</li>
              <li>Notifica√ß√µes importantes</li>
              <li>Experi√™ncia como app nativo</li>
            </ul>
          </div>

          {/* Instru√ß√µes para iOS */}
          {isIOS && (
            <div className="bg-blue-50 dark:bg-blue-950 p-3 rounded-lg text-sm">
              <p className="font-medium text-blue-900 dark:text-blue-100 mb-2">
                Para instalar no iOS:
              </p>
              <ol className="space-y-1 text-blue-800 dark:text-blue-200 text-xs">
                <li>1. Toque no √≠cone de compartilhar ‚ÜóÔ∏è</li>
                <li>2. Role para baixo e toque em "Adicionar √† Tela de In√≠cio"</li>
                <li>3. Toque em "Adicionar"</li>
              </ol>
            </div>
          )}

          {/* Bot√µes */}
          <div className="flex gap-2">
            {/* TEMPOR√ÅRIO: Sempre mostrar bot√£o instalar para depura√ß√£o */}
            {!isIOS && (
              <Button 
                onClick={deferredPrompt ? handleInstallClick : () => {
                  console.log('‚ö†Ô∏è Prompt manual - beforeinstallprompt n√£o dispon√≠vel');
                  console.log('üîß Poss√≠veis problemas: manifest.json, service worker ou crit√©rios PWA n√£o atendidos');
                  alert('PWA n√£o pode ser instalada automaticamente. Verifique se atende aos crit√©rios PWA.');
                }}
                className="flex-1 bg-pink-600 hover:bg-pink-700"
                data-testid="button-install-pwa"
              >
                <Download className="w-4 h-4 mr-2" />
                {deferredPrompt ? 'Instalar App' : 'Debug PWA'}
              </Button>
            )}
            <Button 
              variant="outline" 
              onClick={handleDismiss}
              className={!isIOS ? "flex-1" : "w-full"}
              data-testid="button-maybe-later"
            >
              Agora n√£o
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/useUserTracking.ts.content:
import { useEffect, useRef, useCallback } from "react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";

export interface TrackingConfig {
  trackPageViews?: boolean;
  trackClicks?: boolean;
  trackTime?: boolean;
  sessionId?: string;
}

export function useUserTracking(config: TrackingConfig = {}) {
  const { user } = useAuth();
  const {
    trackPageViews = true,
    trackClicks = true,
    trackTime = true
  } = config;

  const pageStartTime = useRef<number>(Date.now());
  const currentPage = useRef<string>(window.location.pathname);
  const sessionId = useRef<string>(`session-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`);

  // Rastrear visita de p√°gina
  const trackPageVisit = useCallback(async (page: string, duration?: number) => {
    if (!user?.id || !trackPageViews) return;

    try {
      await apiRequest("POST", `/api/analytics/page-visit`, {
        page,
        duration: duration || 0
      });
    } catch (error) {
      console.log("Tracking falhou:", error);
    }
  }, [user?.id, trackPageViews]);

  // Rastrear a√ß√£o do usu√°rio
  const trackAction = useCallback(async (action: string, element?: string) => {
    if (!user?.id || !trackClicks) return;

    try {
      await apiRequest("POST", `/api/analytics/action`, {
        action,
        page: window.location.pathname,
        element
      });
    } catch (error) {
      console.log("Tracking de a√ß√£o falhou:", error);
    }
  }, [user?.id, trackClicks]);

  // Configurar listeners autom√°ticos
  useEffect(() => {
    if (!user?.id) return;

    const startTime = Date.now();
    pageStartTime.current = startTime;
    currentPage.current = window.location.pathname;

    // Rastrear entrada na p√°gina
    trackPageVisit(window.location.pathname);

    // Listener para mudan√ßas de p√°gina
    const handlePopState = () => {
      if (trackTime) {
        const duration = Date.now() - pageStartTime.current;
        trackPageVisit(currentPage.current, duration);
      }

      pageStartTime.current = Date.now();
      currentPage.current = window.location.pathname;
      trackPageVisit(window.location.pathname);
    };

    // Listener para cliques (tracking autom√°tico)
    const handleClick = (event: MouseEvent) => {
      if (!trackClicks) return;

      const target = event.target as HTMLElement;
      const elementId = target.id || target.className || target.tagName;
      const action = target.tagName === 'BUTTON' ? 'button_click' : 
                    target.tagName === 'A' ? 'link_click' : 'click';

      trackAction(action, elementId);
    };

    // Rastrear quando o usu√°rio sai da p√°gina
    const handleBeforeUnload = () => {
      if (trackTime) {
        const duration = Date.now() - pageStartTime.current;
        trackPageVisit(currentPage.current, duration);
      }
    };

    // Rastrear quando a janela perde/ganha foco
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'hidden' && trackTime) {
        const duration = Date.now() - pageStartTime.current;
        trackPageVisit(currentPage.current, duration);
      } else if (document.visibilityState === 'visible') {
        pageStartTime.current = Date.now();
      }
    };

    // Adicionar listeners
    window.addEventListener('popstate', handlePopState);
    document.addEventListener('click', handleClick);
    window.addEventListener('beforeunload', handleBeforeUnload);
    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      // Cleanup e rastrear tempo final na p√°gina
      if (trackTime) {
        const duration = Date.now() - pageStartTime.current;
        trackPageVisit(currentPage.current, duration);
      }

      window.removeEventListener('popstate', handlePopState);
      document.removeEventListener('click', handleClick);
      window.removeEventListener('beforeunload', handleBeforeUnload);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [user?.id, trackPageVisit, trackAction, trackTime, trackClicks]);

  return {
    trackPageVisit,
    trackAction,
    sessionId: sessionId.current
  };
}

client/src/pages/analytics.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowLeft, Activity, Clock, MousePointer, Eye } from "lucide-react";
import { useLocation } from "wouter";
import LoadingSpinner from "@/components/ui/loading-spinner";
import { useAuth } from "@/hooks/useAuth";

interface AnalyticsData {
  id: string;
  action: string;
  page: string;
  element?: string;
  duration?: number;
  timestamp: string;
}

export default function Analytics() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();

  const { data: analyticsData, isLoading } = useQuery<{ analytics: AnalyticsData[] }>({
    queryKey: ["/api/analytics/user"],
    enabled: !!user,
    refetchInterval: 5000 // Atualizar a cada 5 segundos
  });

  if (!user) {
    setLocation("/login");
    return null;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  const analytics = analyticsData?.analytics || [];

  // Estat√≠sticas do usu√°rio
  const pageViews = analytics.filter(item => item.action === 'page_view').length;
  const clicks = analytics.filter(item => item.action.includes('click')).length;
  const totalDuration = analytics
    .filter(item => item.duration)
    .reduce((sum, item) => sum + (item.duration || 0), 0);

  // P√°ginas mais visitadas
  const pageVisits = analytics
    .filter(item => item.action === 'page_view')
    .reduce((acc, item) => {
      acc[item.page] = (acc[item.page] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

  const topPages = Object.entries(pageVisits)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5);

  // Atividades recentes (√∫ltimas 20)
  const recentActivity = analytics
    .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime())
    .slice(0, 20);

  const formatPageName = (path: string) => {
    const pathMap: Record<string, string> = {
      '/': 'Dashboard',
      '/kick-counter': 'Contador de Chutes',
      '/weight-tracking': 'Controle de Peso',
      '/diary': 'Di√°rio',
      '/photo-album': '√Ålbum de Fotos',
      '/baby-development': 'Desenvolvimento do Beb√™',
      '/birth-plan': 'Plano de Parto',
      '/community': 'Comunidade',
      '/profile': 'Perfil'
    };
    return pathMap[path] || path;
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleString('pt-BR');
  };

  const formatDuration = (ms: number) => {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);

    if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    }
    return `${seconds}s`;
  };

  return (
    <div className="min-h-screen bg-cream pb-20">
      <div className="p-4 pt-12">
        <div className="flex items-center justify-between mb-6">
          <Button 
            variant="ghost" 
            size="icon"
            className="rounded-full bg-white shadow-lg"
            onClick={() => setLocation("/")}
            data-testid="button-back"
          >
            <ArrowLeft className="h-5 w-5 text-charcoal" />
          </Button>
          <h2 className="text-xl font-bold text-charcoal" data-testid="text-page-title">
            Analytics do Usu√°rio
          </h2>
          <div className="w-10" />
        </div>

        {/* Estat√≠sticas gerais */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <Card className="bg-white shadow-sm">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-blue-100 rounded-full">
                  <Eye className="h-4 w-4 text-blue-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-charcoal">{pageViews}</p>
                  <p className="text-xs text-gray-600">Visualiza√ß√µes</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white shadow-sm">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-green-100 rounded-full">
                  <MousePointer className="h-4 w-4 text-green-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-charcoal">{clicks}</p>
                  <p className="text-xs text-gray-600">Cliques</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white shadow-sm col-span-2">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-purple-100 rounded-full">
                  <Clock className="h-4 w-4 text-purple-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-charcoal">{formatDuration(totalDuration)}</p>
                  <p className="text-xs text-gray-600">Tempo total navegando</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* P√°ginas mais visitadas */}
        <Card className="bg-white shadow-sm mb-6">
          <CardHeader>
            <CardTitle className="text-lg">P√°ginas Mais Visitadas</CardTitle>
            <CardDescription>Suas p√°ginas favoritas do app</CardDescription>
          </CardHeader>
          <CardContent>
