      kick_counts: 'ü¶µ',
      weight_entries: '‚öñÔ∏è',
      photos: 'üì∏',
      diary_entries: 'üìî',
      symptoms: 'üè•',
      consultations: 'üë©‚Äç‚öïÔ∏è',
