// Gerenciador de autentica√ß√£o sem React Context
export interface User {
  id: string;
  email: string;
  name: string;
  profilePhotoUrl?: string;
}

class AuthManager {
  private user: User | null = null;
  private isLoading: boolean = true;
  private listeners: Set<() => void> = new Set();

  constructor() {
    this.checkAuth();
  }

  private async checkAuth() {
    try {
      console.log("üîç Checking authentication...");
      const response = await fetch("/api/auth/me", {
        credentials: "include",
        cache: "no-cache",
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log("‚úÖ User authenticated:", data);
        this.user = data;
      } else {
        console.log("‚ùå User not authenticated");
        this.user = null;
      }
    } catch (error) {
      console.log("‚ùå Auth check failed:", error);
      this.user = null;
    } finally {
      this.isLoading = false;
      this.notifyListeners();
    }
  }

  async login(email: string, password: string): Promise<void> {
    const response = await fetch("/api/auth/login", {
      method: "POST",
      headers: { 
        "Content-Type": "application/json",
        'Cache-Control': 'no-cache, no-store, must-revalidate'
      },
      credentials: "include",
      body: JSON.stringify({ email, password }),
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error || "Login failed");
    }

    const data = await response.json();
    this.user = data.user;
    this.notifyListeners();

    console.log("‚úÖ Login successful, user:", this.user);
    // Redirecionar para dashboard diretamente
    setTimeout(() => {
      window.location.href = "/";
    }, 200);
  }

  async register(email: string, password: string, name: string): Promise<void> {
    const response = await fetch("/api/auth/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({ email, password, name }),
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error || "Registration failed");
    }

    const data = await response.json();
    this.user = data.user;
    this.notifyListeners();
    window.location.href = "/pregnancy-setup";
  }

  async logout(): Promise<void> {
    await fetch("/api/auth/logout", {
      method: "POST",
      credentials: "include",
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate'
      }
    });

    this.user = null;
    this.notifyListeners();
    // Limpar cache e for√ßar reload completo
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.getRegistrations().then(registrations => {
        registrations.forEach(registration => registration.unregister());
      });
    }
    setTimeout(() => {
      window.location.href = "/login";
      window.location.reload();
    }, 100);
  }

  getUser(): User | null {
    return this.user;
  }

  getIsLoading(): boolean {
    return this.isLoading;
  }

  isAuthenticated(): boolean {
    return !!this.user;
  }

  subscribe(listener: () => void): () => void {
    this.listeners.add(listener);
    return () => {
      this.listeners.delete(listener);
    };
  }

  private notifyListeners() {
    this.listeners.forEach(listener => listener());
  }
}

export const authManager = new AuthManager();

