            <div className="h-3 bg-blue-200 rounded w-2/3"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  const consultation = data?.nextConsultation;

  if (!consultation) {
    return (
      <Card className="bg-gradient-to-r from-gray-50 to-blue-50 border-gray-200">
        <CardHeader className="pb-3">
