                onClick={() => handleArticleClick(article)}
                data-testid={`card-article-${article.id}`}
              >
                <CardHeader>
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      <CategoryIcon className="h-4 w-4" />
                      <Badge className={categoryColors[article.category as keyof typeof categoryColors]}>
                        {article.category === 'nutrition' ? 'Nutri√ß√£o' :
                         article.category === 'exercise' ? 'Exerc√≠cios' :
                         article.category === 'health' ? 'Sa√∫de' :
                         article.category === 'preparation' ? 'Prepara√ß√£o' :
                         'Sintomas'}
                      </Badge>
                    </div>
                    <Badge className={importanceColors[article.importance as keyof typeof importanceColors]}>
                      {article.importance === 'high' ? 'Alta' :
                       article.importance === 'medium' ? 'M√©dia' :
                       'Baixa'}
                    </Badge>
                  </div>
                  <CardTitle className="text-lg text-pink-600 dark:text-pink-400 line-clamp-2">
                    {article.title}
                  </CardTitle>
                  <CardDescription className="line-clamp-3">
                    {article.summary}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-400">
                    <div className="flex items-center gap-1">
                      <Clock className="h-3 w-3" />
                      <span>{article.readingTime} min</span>
                    </div>
                    <div>{article.source}</div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      ) : (
        <Card className="text-center py-12">
          <CardContent>
            <Stethoscope className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-600 dark:text-gray-400 mb-2">
              Nenhum artigo dispon√≠vel
            </h3>
            <p className="text-gray-500 dark:text-gray-500 mb-4">
              N√£o h√° artigos m√©dicos dispon√≠veis para a semana {pregnancyWeek} ainda.
            </p>
            <Button
              onClick={handleSeedArticles}
              disabled={seedArticlesMutation.isPending}
              data-testid="button-seed-articles-empty"
            >
              {seedArticlesMutation.isPending ? (
                <RefreshCw className="mr-2 h-4 w-4 animate-spin" />
              ) : (
                <RefreshCw className="mr-2 h-4 w-4" />
              )}
              Popular Artigos
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

scripts/insert-week-image.js.content:
// Script para inserir imagens do beb√™ por semana
// Como usar:
// 1. Coloque sua imagem na pasta attached_assets/
// 2. Execute: node scripts/insert-week-image.js SEMANA NOME_DA_IMAGEM
// Exemplo: node scripts/insert-week-image.js 5 minha_imagem_semana5.png

import { exec } from 'child_process';

// Pegar argumentos da linha de comando
const week = process.argv[2];
const imageName = process.argv[3];

if (!week || !imageName) {
  console.log('‚ùå Uso: node scripts/insert-week-image.js SEMANA NOME_DA_IMAGEM');
  console.log('üìù Exemplo: node scripts/insert-week-image.js 5 minha_imagem_semana5.png');
  process.exit(1);
}

// Validar semana
const weekNum = parseInt(week);
if (isNaN(weekNum) || weekNum < 1 || weekNum > 40) {
  console.log('‚ùå Semana deve ser um n√∫mero entre 1 e 40');
  process.exit(1);
}

// Criar o caminho da imagem no formato que o sistema espera
const imagePath = `@assets/${imageName}`;

console.log(`üñºÔ∏è Inserindo imagem da semana ${week}: ${imageName}`);

// Fazer requisi√ß√£o para o endpoint correto (baby_image_url)
const command = `curl -X POST -H "Content-Type: application/json" -d '{"week": ${week}, "imageUrl": "${imagePath}"}' http://localhost:5000/api/baby-development/insert-baby-image`;

exec(command, (error, stdout, stderr) => {
  if (error) {
    console.error('‚ùå Erro ao executar comando:', error);
    return;
  }

  try {
    const response = JSON.parse(stdout);
    if (response.success) {
      console.log(`‚úÖ Sucesso! Imagem da semana ${week} inserida no banco de dados`);
      console.log(`üîó URL: ${response.imageUrl}`);
    } else {
      console.log('‚ùå Erro:', response.error || response.message);
    }
  } catch (e) {
    console.log('üìù Resposta:', stdout);
  }
});

client/src/hooks/use-articles.ts.content:
import { useQuery } from "@tanstack/react-query";
import type { Article } from "@shared/schema";

interface ArticlesResponse {
  success: boolean;
  week: number;
  articles: Article[];
}

export function useArticles(week: number) {
  return useQuery<ArticlesResponse>({
    queryKey: ["/api/articles/week", week],
    enabled: !!week && week > 0,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
}

