      'Lentilha': 'üåø',
      'Ervilha': 'üü¢',
      'Mirtilo': 'ü´ê',
      'Framboesa': 'ü´ê',
      'Morango': 'üçì',
      'Lima': 'üü¢',
      'Lim√£o': 'üçã',
      'Ameixa': 'üü£',
      'Abacate': 'ü•ë',
      'Cebola': 'üßÖ',
      'Banana': 'üçå',
      'Espiga de milho': 'üåΩ',
      'Cenoura': 'ü•ï',
      'Berinjela': 'üçÜ',
      'Abobrinha': 'ü•í',
      'Coco': 'ü••',
      'Repolho': 'ü•¨',
      'Melancia': 'üçâ',
      'Ab√≥bora': 'üéÉ'
    };
    return fruitMap[fruitComparison] || 'üçé';
  };

  const handleLogout = async () => {
    try {
